mapper.select:
        select
id, author_id, circle_id, moment_title, content, pic_urls, collect_count, like_count, reply_count, created_by, created_time, update_by, update_time, is_deleted
        from share_moment


                and id = #{id}


                and author_id = #{authorId}


                and circle_id = #{circleId}


                and moment_title = #{momentTitle}


                and content = #{content}


                and pic_urls = #{picUrls}


                and collect_count = #{collectCount}


                and like_count = #{likeCount}


                and reply_count = #{replyCount}


                and created_by = #{createdBy}


                and created_time = #{createdTime}


                and update_by = #{updateBy}


                and update_time = #{updateTime}


                and is_deleted = #{isDeleted}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from share_moment


        and id = #{id}


        and author_id = #{authorId}


        and circle_id = #{circleId}


        and moment_title = #{momentTitle}


        and content = #{content}


        and pic_urls = #{picUrls}


        and collect_count = #{collectCount}


        and like_count = #{likeCount}


        and reply_count = #{replyCount}


        and created_by = #{createdBy}


        and created_time = #{createdTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}
